import { Menu, Button, Input } from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  GlobalOutlined,
  BarsOutlined,
  QrcodeOutlined,
  UserOutlined
} from '@ant-design/icons';
import { useState, useEffect } from 'react';
var QRCode = require('qrcode.react');

const { SubMenu } = Menu;
const axios = require('axios')
const content = {
  marginTop: '100px',
}


const [QRCodeValue, setQRCV] = useState("");
const [inputValue, setInputValue] = useState("input");
const ShareBill = () => {
  
  return (
    <div>
        <Input 
          value={inputValue} 
          onChange={(e)=>{setInputValue(e.target.value); setQRCV(e.target.value)}}
          style={{zIndex:'1', margin:'10px'}}>
        </Input>
        <div className="qrcode">
          <QRCode size="150" level="M" inputMode="text" value={QRCodeValue}  renderAs="svg" bgColor="#fff" fgColor="#136"/>
        </div>
    </div>
  );
}

export default ShareBill;
