import { Menu, Button } from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  GlobalOutlined,
  BarsOutlined,
  QrcodeOutlined,
  UserOutlined
} from '@ant-design/icons';
import { useState, useEffect } from 'react';
var QRCode = require('qrcode.react');

const { SubMenu } = Menu;
const axios = require('axios')
const content = {
  marginTop: '100px',
}

export default function Home() {
  // axios.get('http://numbersapi.com/42')
  // .then(function (response) {
  //   // handle success
  //   console.log(response);
  // })
  // .catch(function (error) {
  //   // handle error
  //   console.log(error); 
  // })
  // .then(function () {
  //   // always executed
  // });
  const [state, setState] = useState({collapsed: true});
  const [QRCodeValue, setQRCV] = useState("empty");

  useEffect(()=>{
    console.log(state);
  },[state]);

  return (
    <div>
      <nav>
        <Button type="primary" onClick={()=>setState({collapsed:!state.collapsed})}>
          {React.createElement(state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined)}
        </Button>
      </nav>
      <div style={{position: 'absolute'}}>
        <Menu
          className={state.collapsed ? "menu_folded" : "menu_unfolded"}
          defaultSelectedKeys={['1']}
          defaultOpenKeys={['sub1']}
          mode="inline"
          theme="dark"
          inlineCollapsed={state.collapsed}
        >
          <Menu.Item key="1" icon={<GlobalOutlined style={{fontSize:"1.3rem"}}/>} className="menu_item_size">
            Map
          </Menu.Item>
          <Menu.Item key="2" icon={<BarsOutlined style={{fontSize:"1.3rem"}}/>} className="menu_item_size">
            List
          </Menu.Item>
          <Menu.Item key="3" icon={<QrcodeOutlined style={{fontSize:"1.3rem"}}/>} className="menu_item_size">
            QR
          </Menu.Item>

          <SubMenu icon={<UserOutlined style={{fontSize:"1.3rem"}}/>} title="Account">
            <Menu.Item key="sub1" onClick={()=>{console.log('Reg')}}>Register</Menu.Item>
            <Menu.Item key="sub2" onClick={()=>{console.log('Log')}}>Log in</Menu.Item>
          </SubMenu>
        </Menu>
      </div>
      <div style={{width: '100vw', height:'calc(100vh - 50px)', backgroundColor:'#ccc'}}>
        <p>QR Code</p>
        <div className="qrcode">
          <QRCode level="M" inputMode="text" value={{QRCodeValue}}  renderAs="svg" bgColor="#fff" fgColor="#136"/>
        </div>
      </div>
    </div>
  );
}
