import { Menu, Button, Input, Slider } from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  GlobalOutlined,
  BarsOutlined,
  QrcodeOutlined,
  UserOutlined
} from '@ant-design/icons';
import { useState, useEffect } from 'react';
var QRCode = require('qrcode.react');

const { SubMenu } = Menu;
const axios = require('axios')
const content = {
  marginTop: '100px',
}



const ShareBill = () => {
    const [QRCodeValue, setQRCV] = useState("");
    const [inputValue, setInputValue] = useState("");
    return (
        <div>
            <Input 
            value={inputValue} 
            onChange={(e)=>{setInputValue(e.target.value); setQRCV(e.target.value)}}
            style={{zIndex:'1', margin:'10px'}}>
            </Input>
            <div className="qrcode">
                <QRCode size="150" level="M" inputMode="text" value={QRCodeValue}  renderAs="svg" bgColor="#fff" fgColor="#136"/>
            </div>
            <Slider min={10} max={20} defaultValue={20} style={{width:'200px'}}></Slider>
        </div>
    );
}

export default ShareBill;
